<?phpdeclare(strict_types=1);require_once ('utils/helpers.php');require_once ('data.php');require_once ('init.php');require_once ('utils/db.php');require_once ('models/categories.php');require_once ('models/lots.php');/** * @var string $title заголовок страницы сайта * @var boolean|mysqli|object $connect ресурс соединения с сервером БД * @var int $is_auth рандомно число 1 или 0 * @var string $user_name имя авторизованного пользователя * @var ?array<int,array{id: int, name: string, code: string} $categories список категорий лотов * @var ?array<int,array{id: int, name: string, category: string, price_start: int, img_url: ?string, date_end: string} $lots массив с параметрами лотов * @var string $main_content HTML-код - контент страницы * @var string $page весь HTML-код страницы с подвалом и шапкой */if (!$connect) {    die(mysqli_connect_error());}$lots = get_lots($connect);$main_content = include_template('main.php', [        'categories' => $categories,        'lots' => $lots,]);$page = include_template('layout.php', [        'title' => $title,        'is_auth' => $is_auth,        'user_name' => $user_name,        'categories' => $categories,        'main_content' => $main_content]);print($page);